$(document).ready(function() {
    var envelope = $('#envelope');
    var btn_open = $("#open");
    var btn_reset = $("#reset");
    var letter = $(".letter");
    var letter_text = $(".words"); // agregar esta l√≠nea

    envelope.click(function() {
      open();
    });
    btn_open.click(function() {
      open();
    });
    btn_reset.click(function() {
      close();
    });

    function open() {
      envelope.addClass("open")
        .removeClass("close");
      letter.addClass("visible");
      letter_text.addClass("show"); // agregar esta l√≠nea
    }

    function close() {
      envelope.addClass("close")
        .removeClass("open");
      letter.removeClass("visible");
      letter_text.removeClass("show"); // agregar esta l√≠nea
    }
  });

